import React from "react";
import * as go from "gojs";
import { ReactDiagram } from "gojs-react";
import PersoneStore from "../store/PersoneStore";

const persones = PersoneStore.persons;
let branchs = [];

const createBranches = () => {
  for (let i = 0; i < persones.length; i++) {
    persone = persones[i];
    branchs[i] = { key: persone.id };
  }
};

function initDiagram() {
  const $ = go.GraphObject.make;
  // set your license key here before creating the diagram: go.Diagram.licenseKey = "...";
  const diagram = $(go.Diagram, {
    "undoManager.isEnabled": true, // must be set to allow for model change listening
    layout: new go.TreeLayout({ angle: 90, layerSpacing: 35 }), // 'undoManager.maxHistoryLength': 0,  // uncomment disable undo/redo functionality
    "clickCreatingTool.archetypeNodeData": {
      text: "new node",
      color: "lightblue",
    },
    model: new go.TreeModel({
      linkKeyProperty: "key", // IMPORTANT! must be defined for merges and data sync when using GraphLinksModel
    }),
  });

  // define a simple Node template
  diagram.nodeTemplate = $(
    go.Node,
    "Auto", // the Shape will go around the TextBlock
    new go.Binding("location", "loc", go.Point.parse).makeTwoWay(
      go.Point.stringify
    ),
    $(
      go.Shape,
      "RoundedRectangle",
      { name: "SHAPE", fill: "white", strokeWidth: 0 },
      // Shape.fill is bound to Node.data.color
      new go.Binding("sourse")
    ),
    $(
      go.TextBlock,
      { margin: 8, editable: true }, // some room around the text
      new go.Binding("text").makeTwoWay()
    )
  );

  diagram.linkTemplate = $(
    go.Link,
    { routing: go.Link.Orthogonal, corner: 5 },
    new go.Shape({ strokeWidth: 3, stroke: "#ccc" })
  );

  return diagram;
}

/**
 * This function handles any changes to the GoJS model.
 * It is here that you would make any updates to your React state, which is discussed below.
 */
function handleModelChange(changes) {
  //alert("GoJS model changed!");
}

const Tree = () => {
  return (
    <div>
      <ReactDiagram
        initDiagram={initDiagram}
        divClassName="diagram-component"
        nodeDataArray={[
          { key: 0, text: "Alpha", loc: "0 0" },
          { key: 1, text: "Beta", loc: "150 0", parent: 0 },
          {
            key: 2,
            text: "Gamma",
            loc: "0 150",
            parent: 0,
          },
          { key: 3, text: "Delta", loc: "150 150", parent: 2 },
        ]}
        onModelChange={handleModelChange}
      />
    </div>
  );
};

export default Tree;
