import React, { useContext } from "react";
import Row from "react-bootstrap/Row";
import { Button, Card, Col, Container, Image } from "react-bootstrap";
import { Context } from "..";

const Persone = () => {
  const isRedact = false;
  const { user } = useContext(Context);
  const persone = {
    id: 1,
    firstname: "Денис",
    lastname: "Колосун",
    surname: "Петрович",
    img: "15e832ed-fd36-4217-8fd2-b0c8397d7598.jpg",
    user: 4,
    updatedAt: "2023-07-29T21:16:29.249Z",
    createdAt: "2023-07-29T21:16:29.249Z",
  };
  const description = [
    { id: 1, title: "Дата рождения", description: "16.04.1985" },
    { id: 3, title: "Город", description: "Кобрин" },
    { id: 4, title: "Область", description: "Брестская" },
    { id: 5, title: "Страна", description: "Белорусь" },
    {
      id: 6,
      title: "Биография",
      description:
        "DDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDSSЖивёт",
    },
  ];
  return (
    <Container className="mt-3 ">
      {isRedact ? (
        <Row>
          <Col md={4}>
            <Image width={300} height={300} src={persone.img} />
            <Form.Group controlId="formFile" className="mb-3">
              <Form.Label>Default file input example</Form.Label>
              <Form.Control type="file" />
            </Form.Group>
          </Col>
        </Row>
      ) : (
        <div>
          <Row>
            <Col md={4}>
              <Image width={300} height={300} src={persone.img} />
            </Col>
            <Col md={4}>
              <Row className="d-flex align-items-center justify-content-center">
                <h2>{persone.lastname}</h2>
                <h2>{persone.firstname}</h2>
                <h2>{persone.surname}</h2>
              </Row>
            </Col>
          </Row>
          <Row className="d-flex flex-column m-3">
            {description.map((info, index) => (
              <Row
                key={info.id}
                style={{
                  backgroundColor:
                    index % 2 === 0
                      ? "rgba(166, 166, 166, 0.2)"
                      : "transparent",
                  wordBreak: "break-all",
                }}
              >
                {info.title}: {info.description}
              </Row>
            ))}
          </Row>
          {user.user.id === persone.user ? (
            <Row className="d-flex justify-content-end m-4">
              <Button variant={"outline-light"} className="w-25">
                Изменить
              </Button>
            </Row>
          ) : (
            <Row></Row>
          )}
        </div>
      )}
    </Container>
  );
};

export default Persone;
