import React from "react";
import Row from "react-bootstrap/Row";
import { Card, Col, Container, Image } from "react-bootstrap";

const Persone = () => {
  const persone = {
    id: 1,
    firstname: "Денис",
    lastname: "Колосун",
    surname: "Петрович",
    img: "15e832ed-fd36-4217-8fd2-b0c8397d7598.jpg",
    updatedAt: "2023-07-29T21:16:29.249Z",
    createdAt: "2023-07-29T21:16:29.249Z",
  };
  const description = [
    { id: 1, title: "Дата рождения", description: "16.04.1985" },
    { id: 2, title: "Дата смерти", description: " " },
    { id: 3, title: "Город", description: "Кобрин" },
    { id: 4, title: "Область", description: "Брестская" },
    { id: 5, title: "Страна", description: "Белорусь" },
    { id: 6, title: "Биография", description: "Живёт" },
  ];
  return (
    <Container className="mt-3 ">
      <Row>
        <Col md={4}>
          <Image width={300} height={300} src={persone.img} />
        </Col>
        <Col md={4}>
          <Row className="d-flex align-items-center justify-content-center">
            <h2>{persone.lastname}</h2>
            <h2>{persone.firstname}</h2>
            <h2>{persone.surname}</h2>
          </Row>
        </Col>
      </Row>
      <Row>
        {description.map(info => {
          <Row>
            {info.title}: {info.description}
          </Row>
        })}
      </Row>
    </Container>
  );
};

export default Persone;
