import React, { useContext } from "react";
import * as go from "gojs";
import { ReactDiagram } from "gojs-react";
import { Context } from "..";
import { PERSONE_ROUTE } from "../utils/consts";

let branchs = [];
//const history = useNavigate;

const createBranches = (persones) => {
  for (let i = 0; i < persones.length; i++) {
    const persone = persones[i];
    let color = "#a1a7cc";
    if (i === 2) {
      color = "#cca1a1";
    }
    branchs[i] = {
      key: persone.id,
      lastname: persone.lastname,
      firstname: persone.firstname,
      surname: persone.surname,
      img: persone.img,
      color: color,
    };
  }
  branchs[1].parent = branchs[0].key;
  branchs[2].parent = branchs[0].key;
};

const textStyle = { margin: new go.Margin(2, 10, 0, 0), stroke: "white" };

function initDiagram() {
  const $ = go.GraphObject.make;
  // set your license key here before creating the diagram: go.Diagram.licenseKey = "...";
  const diagram = $(go.Diagram, {
    "undoManager.isEnabled": true, // must be set to allow for model change listening
    layout: new go.TreeLayout({ angle: 90, layerSpacing: 35 }), // 'undoManager.maxHistoryLength': 0,  // uncomment disable undo/redo functionality
    //"clickCreatingTool.archetypeNodeData": {
    // firstname: "new persone",
    // color: "#a1a7cc",
    //},
    model: new go.TreeModel({
      linkKeyProperty: "key", // IMPORTANT! must be defined for merges and data sync when using GraphLinksModel
    }),
  });

  // define a simple Node template
  diagram.nodeTemplate = $(
    go.Node,
    "Auto", // the Shape will go around the TextBlock
    {
      // here the second argument is this object, which is this Node
      doubleClick: (e, node) => {
        window.open(".." + PERSONE_ROUTE + "/" + node.key);
      },
    },
    new go.Binding("location", "loc", go.Point.parse).makeTwoWay(
      go.Point.stringify
    ),
    $(
      go.Shape,
      "RoundedRectangle",
      {
        name: "SHAPE",
        fill: "#ccc",
        strokeWidth: 0,
      },
      new go.Binding("fill", "color")
    ),
    $(
      go.Panel,
      "Horizontal",
      $(
        go.Picture,
        { margin: 10, width: 50, height: 50, background: "white" },
        new go.Binding("sourse", "img")
      ),
      $(
        go.Panel,
        "Vertical",
        $(
          go.TextBlock,
          textStyle, // some room around the text
          new go.Binding("text", "lastname").makeTwoWay()
        ),
        $(
          go.TextBlock,
          textStyle, // some room around the text
          new go.Binding("text", "firstname").makeTwoWay()
        ),
        $(
          go.TextBlock,
          textStyle, // some room around the text
          new go.Binding("text", "surname").makeTwoWay()
        )
      )
    )
  );

  diagram.nnodeTemplate.contextMenu = $(
    "ContextMenu",
    $("ContextMenuButton", $(go.TextBlock, "Добавить Родствинека"), {
      click: (e, button) => {
        const node = button.part.adornedPart;
        if (node !== null) {
          const thisemp = node.data;
          myDiagram.startTransaction("add employee");
          const newemp = {
            name: "(new person)",
            title: "",
            comments: "",
            parent: thisemp.key,
          };
          myDiagram.model.addNodeData(newemp);
          const newnode = myDiagram.findNodeForData(newemp);
          if (newnode) newnode.location = node.location;
          myDiagram.commitTransaction("add employee");
        }
      },
    }),
    $("ContextMenuButton", $(go.TextBlock, "Vacate Position"), {
      click: (e, button) => {
        const node = button.part.adornedPart;
        if (node !== null) {
          const thisemp = node.data;
          myDiagram.startTransaction("vacate");
          // update the key, name, picture, and comments, but leave the title
          myDiagram.model.setDataProperty(thisemp, "name", "(Vacant)");
          myDiagram.model.setDataProperty(thisemp, "pic", "");
          myDiagram.model.setDataProperty(thisemp, "comments", "");
          myDiagram.commitTransaction("vacate");
        }
      },
    }),
    $("ContextMenuButton", $(go.TextBlock, "Remove Role"), {
      click: (e, button) => {
        // reparent the subtree to this node's boss, then remove the node
        const node = button.part.adornedPart;
        if (node !== null) {
          myDiagram.startTransaction("reparent remove");
          const chl = node.findTreeChildrenNodes();
          // iterate through the children and set their parent key to our selected node's parent key
          while (chl.next()) {
            const emp = chl.value;
            myDiagram.model.setParentKeyForNodeData(
              emp.data,
              node.findTreeParentNode().data.key
            );
          }
          // and now remove the selected node itself
          myDiagram.model.removeNodeData(node.data);
          myDiagram.commitTransaction("reparent remove");
        }
      },
    }),
    $("ContextMenuButton", $(go.TextBlock, "Remove Department"), {
      click: (e, button) => {
        // remove the whole subtree, including the node itself
        const node = button.part.adornedPart;
        if (node !== null) {
          myDiagram.startTransaction("remove dept");
          myDiagram.removeParts(node.findTreeParts());
          myDiagram.commitTransaction("remove dept");
        }
      },
    }),
    $("ContextMenuButton", $(go.TextBlock, "Toggle Assistant"), {
      click: (e, button) => {
        // remove the whole subtree, including the node itself
        const node = button.part.adornedPart;
        if (node !== null) {
          myDiagram.startTransaction("toggle assistant");
          myDiagram.model.setDataProperty(
            node.data,
            "isAssistant",
            !node.data.isAssistant
          );
          myDiagram.layout.invalidateLayout();
          myDiagram.commitTransaction("toggle assistant");
        }
      },
    })
  );

  diagram.linkTemplate = $(
    go.Link,
    { routing: go.Link.Orthogonal, corner: 5 },
    new go.Shape({ strokeWidth: 3, stroke: "#ccc" })
  );

  return diagram;
}

/**
 * This function handles any changes to the GoJS model.
 * It is here that you would make any updates to your React state, which is discussed below.
 */
function handleModelChange(changes) {
  // console.log(changes);
}

const Tree = () => {
  const { persone } = useContext(Context);
  createBranches(persone.persones);
  return (
    <div>
      <ReactDiagram
        initDiagram={initDiagram}
        divClassName="diagram-component"
        nodeDataArray={branchs}
        onModelChange={handleModelChange}
      />
    </div>
  );
};

export default Tree;
