import React, { useContext, useEffect, useState } from "react";
import Row from "react-bootstrap/Row";
import { Button, Col, Container, Form, Image } from "react-bootstrap";
import { Context } from "..";
import { checkId } from "../routes";
import { fetchOnePersone } from "../http/personeAPI";

const Persone = () => {
  const [isRedact, setIsRedact] = useState();
  const [render, setRender] = useState();
  const { user } = useContext(Context);

  let descriptionData = [
    { id: 1, title: "Дата рождения", description: "16.04.1985" },
    { id: 3, title: "Город", description: "Кобрин" },
    { id: 4, title: "Область", description: "Брестская" },
    { id: 5, title: "Страна", description: "Белорусь" },
    {
      id: 6,
      title: "Биография",
      description:
        "DDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDSSЖивёт",
    },
  ];

  const [persone, setPersone] = useState({});
  const [description, setDescription] = useState(descriptionData);

  useEffect(() => {
    fetchOnePersone(checkId()).then((data) => {
      setPersone(data);
    });
  });
  return (
    <Container className="mt-3 ">
      {isRedact ? (
        <div>
          <Row>
            <Col md={4}>
              <Image width={300} height={300} src={persone.img} />
              <Form.Group controlId="formFile" className="mt-3 ">
                <Form.Control
                  type="file"
                  className="bg-transparent text-white"
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Row className="d-flex align-items-center justify-content-center">
                <Form.Group>
                  <Form.Control
                    className="bg-transparent text-white form-control-lg"
                    defaultValue={persone.lastname}
                    onChange={(e) => {
                      persone.lastname = e.target.value;
                    }}
                  />
                  <Form.Control
                    className="bg-transparent text-white form-control-lg"
                    defaultValue={persone.firstname}
                    onChange={(e) => {
                      persone.firstname = e.target.value;
                    }}
                  />
                  <Form.Control
                    className="bg-transparent text-white form-control-lg"
                    defaultValue={persone.surname}
                    onChange={(e) => {
                      persone.surname = e.target.value;
                    }}
                  />
                  <Container>
                    <Form.Check
                      defaultChecked={persone.gender === "man" ? true : false}
                      className="m-3"
                      inline
                      label="Мужчина"
                      name="group1"
                      type="radio"
                      id={"inline-radio-1"}
                      onChange={() => {
                        persone.gender = "man";
                      }}
                    />
                    <Form.Check
                      defaultChecked={persone.gender === "woman" ? true : false}
                      className="m-3"
                      inline
                      label="Женщина"
                      name="group1"
                      type="radio"
                      id={"inline-radio-2"}
                      onClick={() => {
                        persone.gender = "woman";
                      }}
                    />
                  </Container>
                </Form.Group>
              </Row>
            </Col>
          </Row>
          <Form.Group className="d-flex flex-column m-3">
            {description.map((info, index) => (
              <Row className="" key={info.id}>
                <Col className="col-sm-11 m-0 p-0">
                  <Form.Control
                    as="textarea"
                    rows={1}
                    size="sm"
                    className="bg-transparent text-white mr-5"
                    defaultValue={info.title + ": " + info.description}
                    onChange={(e) => {
                      const des = e.target.value.split(": ");
                      description[index].title = des[0];
                      description[index].description = des[1];
                    }}
                  />
                </Col>
                <Col className="p-0 m-0">
                  <Button
                    className="btn-close btn-close-white mt-1"
                    variant={"outline-light"}
                    onClick={() => {
                      description.splice(index, 1);
                      setRender(!render);
                    }}
                  ></Button>
                </Col>
              </Row>
            ))}
          </Form.Group>
          <Button
            className="mx-1"
            variant={"outline-light"}
            onClick={() => {
              description.push({
                id: Math.floor(Math.random() * 1000),
                title: "",
                description: "",
              });
              setRender(!render);
            }}
          >
            Добавить пункт
          </Button>
          <Container className="d-flex justify-content-end m-3">
            <Button
              variant={"outline-success"}
              onClick={() => {
                descriptionData = description;
                personeData = persone;
                setIsRedact(false);
              }}
            >
              Сохранить изменения
            </Button>
            <Button
              variant={"outline-danger"}
              className="mx-3"
              onClick={() => {
                setDescription(descriptionData);
                setPersone(personeData);
                setIsRedact(false);
              }}
            >
              Отмена
            </Button>
          </Container>
        </div>
      ) : (
        <div>
          <Row>
            <Col md={4}>
              <Image width={300} height={300} src={persone.img} />
            </Col>
            <Col md={4}>
              <Row className="d-flex align-items-center justify-content-center">
                <h2>{persone.lastname}</h2>
                <h2>{persone.firstname}</h2>
                <h2>{persone.surname}</h2>
                <p className="m-0">
                  {persone.gender === "man" ? "Мужчина" : "Женщина"}
                </p>
              </Row>
            </Col>
          </Row>
          <Row className="d-flex flex-column m-3">
            {description.map((info, index) => (
              <Row
                key={info.id}
                style={{
                  backgroundColor:
                    index % 2 === 0
                      ? "rgba(166, 166, 166, 0.2)"
                      : "transparent",
                  wordBreak: "break-all",
                }}
              >
                {info.title}: {info.description}
              </Row>
            ))}
          </Row>
          {user.user.id === persone.user ? (
            <Row className="d-flex justify-content-end m-4">
              <Button
                onClick={() => {
                  setIsRedact(true);
                }}
                variant={"outline-light"}
                className="w-25"
              >
                Изменить
              </Button>
            </Row>
          ) : (
            <Row></Row>
          )}
        </div>
      )}
    </Container>
  );
};

export default Persone;
